import "./App.css";
import { useState, useEffect, Fragment } from "react";
import { getAll, update } from "./BooksAPI";
import { Routes, Route, Link } from "react-router-dom";
import PropTypes from 'prop-types';
import SearchsBooks from "./components/SearchBooks";
import Shelves from "./components/Shelves";


const App = () => {

const [mapOfIdBooks, setMapOfIdBooks] = useState([]);
const [mergedBooks, setMergedBooks] = useState([]);

const [mapOfIdToBooks, setMapOfIdToBooks ] = useState(new Map());
  const [books, setBooks] = useState([]);

  useEffect(() => {
    const allBooks = async () => {
      const res = await getAll();

      return setBooks(res);
      setMapOfIdToBooks(createMapOfBooks(res));
    };
    allBooks();
  }, []);

  useEffect(() => {
    const mixData = books.map(book => {
      if(mapOfIdBooks.has(book.id)){
        return mapOfIdToBooks.get(book.id)
      } else{
        return book;
      }
    })
  });

  const createMapOfBooks = (data)=> {
    const map =  new Map();
    data.map(book => {
       map.set(book.id, book);
       return map;
    })
  }


  const moveAtherShelf = (book,  shelfTo) => {
    const putShelf = books.map((bk) => {
      // console.log(bk);
      if (bk.id === book.id) {
          book.shelf = shelfTo;
          return book;
      }
      return bk;
    });
    setBooks(putShelf);
    update(book, shelfTo);
  };


  return (
    <div className="app">
      <Routes>
        <Route
          exact
          path="/"
          element={
            <Fragment>
              <div className="list-books-title">
                <h1>MyReads</h1>
              </div>
              <div className="list-books-content">
                <Shelves books={books} moveAtherShelf={moveAtherShelf} />
                <div className="open-search">
                  <Link to="/search">
                    <button>Add A Book</button>
                  </Link>
                </div>
              </div>
            </Fragment>
          }
        />

        <Route exact path="/search" element={<SearchsBooks />} />
      </Routes>
    </div>
  );
};

App.propTypes = {
  books: PropTypes.array,
  moveAtherShelf: PropTypes.func

}

export default App;
