import React from 'react';
import Shelf from './Shelf';

function Shelves({books, moveAtherShelf}) {


    // const currentlyRead = books.filter((book) => book.shelf === "currentlyReading" );
    // const wantRead = books.filter((book) => book.shelf === "wantToRead" );
    // const read = books.filter((book) => book.shelf === "read" );

    switch(books){
        case currentlyRead:
            books.Shelf = books.filter((book) => book.shelf === "currentlyReading" );
            break;
        case wantRead:
            books.Shelf = books.filter((book) => book.shelf === "wantToRead" );
            break;
        case read: 
             books.shelf = books.filter((book) => book.shelf === "read" );
             break;

    }

    return (
      <div>
        <Shelf title="Currently Reading" books={currentlyRead} moveAtherShelf={moveAtherShelf} />
        <Shelf title="Want To Read" books={wantRead} moveAtherShelf={moveAtherShelf}/>
        <Shelf title="Read" books={read} moveAtherShelf={moveAtherShelf}/>
      
      </div>
    )

}

export default Shelves
