 const [value, setValue] = useState('');
 const getValue = (e) => {
     e.preventDefault();
     setValue(e.target.value);
    //  console.log(value)
    
    const searchBook = async (value, maxNum) => {
        const res = await search(value);
     console.log(res)
    }
    searchBook(value, 5)
    
 }


  return (
      <Fragment>
    <input
    type="text" value={value} onChange={(e) =>  getValue(e)}
    placeholder="Search by title, author, or ISBN"
  />
 <div>
     {searhBook.map(book => {
         <h2>{book.authors}</h2>
     })}
 </div>


</Fragment>
  )
}

