import React, { Fragment, useEffect, useState } from "react";
// import {Link} from 'react-router-dom'
import Book from "./Book";
import { search, getAll } from "../BooksAPI";

const SearchBooks = ({ moveAtherShelf }) => {
  const [books, setBooks] = useState([]);
  const [allBooks, setAllBooks] = useState([]);
  const [founded, setFounded] = useEffect([]);

  const [value, setValue] = useState("");

  let loading;
  useEffect(() => {
    loading = true;
    if (value) {
      const dataFetch = async () => {
        const res = await search(value);
        if (res.error) {
          setBooks([]);
        } else {
          setBooks(res);
        }
      };
      dataFetch();
    }

    const clear = () => {
      return (loading = false);
    };
    clear();
  }, [value]);

  useEffect(()=> {

    const allData = async () => {
        const res = await getAll();
       return  setAllBooks(res)
      }
      allData();
     
      const moveAtherShelfs = (books, shelfTo)=> {
        const putShelf =  allBooks.map(bk => {
        console.log(bk)
          if(bk.id === book.id){
            book.shelf = shelfTo
            return book;
          }
          return bk;
        });
        setBooks(putShelf);
        // update(book, shelfTo);
       }

       moveAtherShelfs(books, shelfTo)

   
})

  return (
    <Fragment>
      <input
        type="text"
        value={value}
        onChange={(e) => setValue(e.target.value)}
        placeholder="Search by title, author, or ISBN"
      />

      <div className="bookshelf-books">
        <ol className="books-grid">
          {books.map((bb) => (
            <li key={bb.id}>
              {" "}
              <Book book={bb} moveAtherShelf={moveAtherShelf} />
            </li>
          ))}
        </ol>
      </div>
    </Fragment>
  );
};

export default SearchBooks;
