import "./App.css";
import { useState, useEffect, Fragment } from "react";
import PropTypes from 'prop-types';
import { getAll, update } from "./BooksAPI";
import { Routes, Route, Link } from "react-router-dom";
import useDebounce from "./useDebounce";
import { search } from "../BooksAPI";

// import ListBook from "./components/ListBook";
import SearchsBooks from "./components/SearchBooks";
import Shelves from "./components/Shelves";

const App = () => {


  const [books, setBooks] = useState([]);
  const [searched, setSearch] = useState([]);
  // console.log(books)
  useEffect(() => {
    const allBooks = async () => {
      const res = await getAll();

      return setBooks(res);
    };
    allBooks();
  }, []);
 
// search

const debouncedValue = useDebounce(text, 500);

useEffect(() => {
  let loading = true;
  if (text && loading) {
    const dataFetch = async () => {
      const res = await search(text);
      if (res.error) {
        setBooks([]);
      } else {
        setSearch(res);
      }
    };
    dataFetch();
  }

  const clear = () => {
    return (loading = false);
  };
  clear();
}, [debouncedValue]);

  const moveAtherShelf = ( book, shelfTo) => {
    const putShelf = books.map((bk) => {
      // console.log(bk);
      if (bk.id === book.id) {
        book.shelf = shelfTo;
        return book
      }
      return bk;
    });
    setBooks(putShelf);
    update(book, shelfTo);
  };



  return (
    <div className="app">
      <Routes>
        <Route
          exact
          path="/"
          element={
            <Fragment>
              <div className="list-books-title">
                <h1>MyReads</h1>
              </div>
              <div className="list-books-content">
                <Shelves books={books} moveAtherShelf={moveAtherShelf} />
                <div className="open-search">
                  <Link to="/search">
                    <button>Add A Book</button>
                  </Link>
                </div>
              </div>
            </Fragment>
          }
        />

        <Route exact path="/search" element={<SearchsBooks moveAtherShelf={moveAtherShelf} BooksOnshelf={books}/> } />
      </Routes>
    </div>
  );
};

App.propTypes = {
  books: PropTypes.array,
  moveAtherShelf: PropTypes.func

}

export default App;
