import React, { Fragment } from 'react';
// import Shelf from './Shelf';
import Book from './Book'

function Shelves({books, moveAtherShelf}) {

  let currentlyRead, 
      wantRead,
      read;


    const shelfChange = (books) => {
       currentlyRead = books.filter((book) => book.shelf === "currentlyReading" );
       wantRead = books.filter((book) => book.shelf === "wantToRead" );
       read = books.filter((book) => book.shelf === "read" );
    }

    shelfChange(books)


    return (

      <Fragment>
        <div className="bookshelf">
    <h2 className="bookshelf-title">{title}</h2>
    <div className="bookshelf-books">
      <ol className="books-grid">
      {books.map((bb) => 
      <li key={bb.id}> <Book book={bb} moveAtherShelf={moveAtherShelf} />

      </li>)}
   

      </ol>
    </div>
  </div>
      <div>
        <Shelf title="Currently Reading" books={currentlyRead} moveAtherShelf={moveAtherShelf} />
        <Shelf title="Want To Read" books={wantRead} moveAtherShelf={moveAtherShelf}/>
        <Shelf title="Read" books={read} moveAtherShelf={moveAtherShelf}/>
      
      </div>
      </Fragment>
    )

}

export default Shelves
