 const [value, setValue] = useState('');

useEffect( async()=> {
    if(value){
        const res = await search(value);
        setBooks(res)
    }

}, [value])

  return (
      <Fragment>
    <input
    type="text" value={value} onChange={(e) =>  setValue(e.target.value)}
    placeholder="Search by title, author, or ISBN"
  />
