import React, { Fragment, useState } from 'react';
import {search} from '../BooksAPI'

const  SearchBooks = ({searhBook}) => {

    const store = {
        book: []
    }
 const [value, setValue] = useState('');
 const getValue = (e) => {
     e.preventDefault();
     setValue(e.target.value);
    //  console.log(value)
    
  
    const searchBook = async (value, maxNum) => {
        const res = await search(value);
    //  console.log(res)
    //  store.book.push(res);
  res.map((bk) => {
     return store.book(bk)
  })


    }
    searchBook(value, 5)

 }


  return (
      <Fragment>
    <input
    type="text" value={value} onChange={(e) =>  getValue(e)}
    placeholder="Search by title, author, or ISBN"
  />
 <div>
     {store.book.forEach(bok => {
         <h2>{bok.id}</h2>
     })}
 </div>
</Fragment>
  )
}

export default SearchBooks
