import React from 'react';
import Group from './Group';

function Shelves({books, moveAtherShelf}) {

  let currentlyRead, 
      wantRead,
      read;


    const shelfChange = (books) => {
       currentlyRead = books.filter((book) => book.shelf === "currentlyReading" );
       wantRead = books.filter((book) => book.shelf === "wantToRead" );
       read = books.filter((book) => book.shelf === "read" );
    }

    shelfChange(books)


    return (
      <div>
        <Group shelf="Currently Reading" books={currentlyRead} moveAtherShelf={moveAtherShelf} />
        <Group shelf="Want To Read" books={wantRead} moveAtherShelf={moveAtherShelf}/>
        <Group shelf="Read" books={read} moveAtherShelf={moveAtherShelf}/>
      
      </div>
    )

}

export default Shelves
