import {search} from '../BooksAPI'

const  SearchBooks = () => {

const Book = [];
 const [value, setValue] = useState('');
 const getValue = (e) => {
     e.preventDefault();
     setValue(e.target.value);
    //  console.log(value)
    
  
    const searchBook = async (value) => {
        const res = await search(value);

     Book.push(res);
    return res
     
    }
    searchBook(value)

 }

 console.log(Book.map(bk => bk.title))

  return (
      <Fragment>
    <input
    type="text" value={value} onChange={(e) =>  getValue(e)}
    placeholder="Search by title, author, or ISBN"
  />
 <div>
   {Book.map(bk => {
     return   <h2>{bk.title}</h2>
   })}
 </div>


</Fragment>
  )
}

