import "./App.css";
import { useState, useEffect } from "react";
import {getAll, update} from './BooksAPI';
import {Link} from 'react-router-dom'

// import ListBook from "./components/ListBook";
import SearchsBooks from "./components/SearchBooks";
import Head from "./components/Head";
import Shelves from './components/Shelves';

const App = () => {
  const [showSearchPage, setShowSearchpage] = useState('');
 
  
  const [books, setBooks] = useState([]);
  // console.log(books)
  useEffect(()=> {
   const allBooks = async () => {
     const res = await getAll();
    return  setBooks(res)
   }
   allBooks()
  },[]);
  // console.log(books);
  const moveAtherShelf = (book, shelfTo)=> {
    const putShelf =  books.map(bk => {
    console.log(bk)
      if(bk.id === book.id){
        book.shelf = shelfTo
        return book;
      }
      return bk;
    });
    setBooks(putShelf);
    update(book, shelfTo);
   }

 
  // useEffect(() => {
  // const allBooks = async () => {
  //   const res = await getAll();
  //   store.Books.push(res);
  //   // console.log(store.Books)
  // }

  // allBooks();
  // });

  const searchBook = () => {
    setShowSearchpage('search');
  }

  return (
    <div className="app">
      {showSearchPage === 'search' ? (
        <div className="search-books">
          <div className="search-books-bar">
            <Link
              className="close-search"
              to="/"
              onClick={() => setShowSearchpage(!showSearchPage)}
            >
              Close
            </Link>
            <Link className="search-books-input-wrapper" to="/search">
         <SearchsBooks books={books} />
            </Link>
          </div>
          <div className="search-books-results">
            <ol className="books-grid"></ol>
          </div>
        </div>
      ) : (
    <Head linkToSearch={searchBook} />
      )}
<div className="list-books-content">
 <Shelves books={books} moveAtherShelf={moveAtherShelf} />
</div>
    </div>
  );
}

export default App;
