import {Link} from 'react-router-dom'

// import ListBook from "./components/ListBook";
import SearchsBooks from "./components/SearchBooks";
import Head from "./components/Head";
import Shelves from './components/Shelves';

const App = () => {
  const [showSearchPage, setShowSearchpage] = useState('');

  const moveAtherShelf =(book, shelfTo)=> {
   const properPlace =  store.Books.map((bk) => {
     if(bk.id === book.id){
       book.shelf = shelfTo
       return book
     }
   })
  }
  const store = {
    searchBooks: [],
    Books: [
      {
        url: "http://books.google.com/books/content?id=PGR2AwAAQBAJ&printsec=frontcover&img=1&zoom=1&imgtk=AFLRE73-GnPVEyb7MOCxDzOYF1PTQRuf6nCss9LMNOSWBpxBrz8Pm2_mFtWMMg_Y1dx92HT7cUoQBeSWjs3oEztBVhUeDFQX6-tWlWz1-feexS0mlJPjotcwFqAg6hBYDXuK_bkyHD-y&source=gbs_api",
        title: "To Kill a Mockingbird",
        author: "Harper Lee",
        shelf: "currentlyReading"
    },
    {
      url: "http://books.google.com/books/content?id=yDtCuFHXbAYC&printsec=frontcover&img=1&zoom=1&imgtk=AFLRE72RRiTR6U5OUg3IY_LpHTL2NztVWAuZYNFE8dUuC0VlYabeyegLzpAnDPeWxE6RHi0C2ehrR9Gv20LH2dtjpbcUcs8YnH5VCCAH0Y2ICaKOTvrZTCObQbsfp4UbDqQyGISCZfGN&source=gbs_api",
      title: "Ender's Game",
      author: "Harper LeeOrson Scott Card",
      shelf: "currentlyReading"
  },

  { 
    url: "http://books.google.com/books/content?id=uu1mC6zWNTwC&printsec=frontcover&img=1&zoom=1&imgtk=AFLRE73pGHfBNSsJG9Y8kRBpmLUft9O4BfItHioHolWNKOdLavw-SLcXADy3CPAfJ0_qMb18RmCa7Ds1cTdpM3dxAGJs8zfCfm8c6ggBIjzKT7XR5FIB53HHOhnsT7a0Cc-PpneWq9zX&source=gbs_api",
  title: "1776",
  author: "David McCullough",
  shelf: "wantToRead"
},

{ 
  url: "http://books.google.com/books/content?id=wrOQLV6xB-wC&printsec=frontcover&img=1&zoom=1&imgtk=AFLRE72G3gA5A-Ka8XjOZGDFLAoUeMQBqZ9y-LCspZ2dzJTugcOcJ4C7FP0tDA8s1h9f480ISXuvYhA_ZpdvRArUL-mZyD4WW7CHyEqHYq9D3kGnrZCNiqxSRhry8TiFDCMWP61ujflB&source=gbs_api",
title: "Harry Potter and the Sorcerer's Stone",
author: "J.K. Rowling",
shelf: "wantToRead"
},

{ 
  url: "http://books.google.com/books/content?id=pD6arNyKyi8C&printsec=frontcover&img=1&zoom=1&imgtk=AFLRE70Rw0CCwNZh0SsYpQTkMbvz23npqWeUoJvVbi_gXla2m2ie_ReMWPl0xoU8Quy9fk0Zhb3szmwe8cTe4k7DAbfQ45FEzr9T7Lk0XhVpEPBvwUAztOBJ6Y0QPZylo4VbB7K5iRSk&source=gbs_api",
title: "The Hobbit",
author: "J.R.R. Tolkien",
shelf: "read"
},

{ 
  url: "http://books.google.com/books/content?id=1q_xAwAAQBAJ&printsec=frontcover&img=1&zoom=1&imgtk=AFLRE712CA0cBYP8VKbEcIVEuFJRdX1k30rjLM29Y-dw_qU1urEZ2cQ42La3Jkw6KmzMmXIoLTr50SWTpw6VOGq1leINsnTdLc_S5a5sn9Hao2t5YT7Ax1RqtQDiPNHIyXP46Rrw3aL8&source=gbs_api",
title: "Oh, the Places You'll Go!",
author: "seuss",
shelf: "read"
},

{ 
  url: "http://books.google.com/books/content?id=32haAAAAMAAJ&printsec=frontcover&img=1&zoom=1&imgtk=AFLRE72yckZ5f5bDFVIf7BGPbjA0KYYtlQ__nWB-hI_YZmZ-fScYwFy4O_fWOcPwf-pgv3pPQNJP_sT5J_xOUciD8WaKmevh1rUR-1jk7g1aCD_KeJaOpjVu0cm_11BBIUXdxbFkVMdi&source=gbs_api",
title: "The Adventures of Tom Sawyer",
author: "Mark Twain",
shelf: "read"
},

    ]
  }

 
  useEffect(() => {
  const allBooks = async () => {
    const res = await getAll();
    store.Books.push(res);
    // console.log(store.Books)
  }

  allBooks();
  });

  const searchBook = () => {
    setShowSearchpage('search');
  }

  return (
    <div className="app">
      {showSearchPage === 'search' ? (
        <div className="search-books">
          <div className="search-books-bar">
            <Link
              className="close-search"
              to="/"
              onClick={() => setShowSearchpage(!showSearchPage)}
            >
              Close
            </Link>
            <Link className="search-books-input-wrapper" to="/search">
         <SearchsBooks />
            </Link>
          </div>
          <div className="search-books-results">
            <ol className="books-grid"></ol>
          </div>
        </div>
      ) : (
    <Head linkToSearch={searchBook} />
      )}
<div className="list-books-content">
 <Shelves books={store.Books} />
</div>
    </div>
  );
}

export default App;
